mapper.select:
SELECT *
FROM derivedRequest
WHERE 1

	AND originUrl = #{originUrl}


	AND width = #{width}


	AND height = #{height}

ORDER BY id ASC LIMIT 1; # 지정한 width 와 height 크기로 이미지가 생성된 최초 파생 요청(derivedRequest)

SELECT * FROM derivedRequest WHERE 1 AND originUrl = #{originUrl} AND height = 0 # 특정 height 가 지정되지 않을 경우에 height 는 0 (원본 비율이 유지된 경우)
AND width <= #{maxWidth}
ORDER BY width DESC, id ASC LIMIT 1; # maxWidth 이하인 width 중 가장 큰 width 로 이미지가 생성된 최초 파생 요청(derivedRequest)
