mapper.select:
SELECT *
FROM genFile
WHERE 1

	AND relTypeCode = #{relTypeCode}


	AND relId = #{relId}


	AND typeCode = #{typeCode}


	AND type2Code = #{type2Code}


	AND fileNo = #{fileNo}

SELECT *
FROM genFile
WHERE 1

	AND relTypeCode = #{relTypeCode}


	AND relId = #{relId}


	AND fileExtTypeCode = #{fileExtTypeCode}


	AND width = #{width}


	AND height = #{height}

SELECT *
FROM genFile
WHERE 1
AND relTypeCode = #{relTypeCode}
AND relId IN

	#{relId}

AND fileExtTypeCode = #{fileExtTypeCode}

  AND width <= #{maxWidth}
